TextStyle getStyle({
  Color? color,
  double? fontSize,
  FontWeight? fontWeight,
  TextDecoration? decoration,
  Color? decorationColor,
}) {
  return GoogleFonts.inter(color: color, fontSize: fontSize, fontWeight: fontWeight, decoration: decoration, decorationColor: decorationColor);
}
